    private void cetakdataActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cetakdataActionPerformed
        // TODO add your handling code here:
        try
        {
            String user = "root";
            String pass = "";
            String dbURL = "jdbc:mysql://localhost/db10118295penyewaankendaraan";
            Class.forName("com.mysql.jdbc.Driver");
            Connection conn = DriverManager.getConnection(dbURL, user, pass);
            
            HashMap param = new HashMap();
            
            JRadioButton penyewa = new JRadioButton("Penyewa");
            JRadioButton petugas = new JRadioButton("Petugas");
            JRadioButton merek_mobil = new JRadioButton("Merek Mobil");
            JRadioButton tipe_mobil = new JRadioButton("Tipe Mobil");
            JRadioButton mobil = new JRadioButton("Mobil");
            JRadioButton sewa = new JRadioButton("Sewa");
                
            JPanel cetak = new JPanel();
            cetak.setLayout(new BoxLayout(cetak, BoxLayout.PAGE_AXIS));
            cetak.setBorder(BorderFactory.createEmptyBorder(0, 20, 20, 20));
            cetak.add(penyewa);
            cetak.add(petugas);
            cetak.add(merek_mobil);
            cetak.add(tipe_mobil);
            cetak.add(mobil);
            cetak.add(sewa);
            
            int result = JOptionPane.showConfirmDialog(null, cetak,
            "Pilih Data Yang Ingin Dicetak", JOptionPane.OK_CANCEL_OPTION);
            if (result == JOptionPane.OK_OPTION){
  
                if (sewa.isSelected())
                {
                    File file = new File ("src/cetakSewa.jasper");

                    JasperReport jp = (JasperReport)JRLoader.loadObject(file);
                    JasperPrint jasperprint = JasperFillManager.fillReport(jp, param, conn);
                    JasperViewer.viewReport(jasperprint,false);
                    JasperViewer.setDefaultLookAndFeelDecorated(true); 
                }
                else if(mobil.isSelected())
                {
                    File file = new File ("src/cetakMobil.jasper");

                    JasperReport jp = (JasperReport)JRLoader.loadObject(file);
                    JasperPrint jasperprint = JasperFillManager.fillReport(jp, param, conn);
                    JasperViewer.viewReport(jasperprint,false);
                    JasperViewer.setDefaultLookAndFeelDecorated(true); 
                }
                else if(merek_mobil.isSelected())
                {
                    File file = new File ("src/cetakMerekmobil.jasper");

                    JasperReport jp = (JasperReport)JRLoader.loadObject(file);
                    JasperPrint jasperprint = JasperFillManager.fillReport(jp, param, conn);
                    JasperViewer.viewReport(jasperprint,false);
                    JasperViewer.setDefaultLookAndFeelDecorated(true); 
                }
                else if(tipe_mobil.isSelected())
                {
                    File file = new File ("src/cetakTipemobil.jasper");

                    JasperReport jp = (JasperReport)JRLoader.loadObject(file);
                    JasperPrint jasperprint = JasperFillManager.fillReport(jp, param, conn);
                    JasperViewer.viewReport(jasperprint,false);
                    JasperViewer.setDefaultLookAndFeelDecorated(true); 
                }                
                else if(penyewa.isSelected())
                {
                    File file = new File ("src/cetakPenyewa.jasper");

                    JasperReport jp = (JasperReport)JRLoader.loadObject(file);
                    JasperPrint jasperprint = JasperFillManager.fillReport(jp, param, conn);
                    JasperViewer.viewReport(jasperprint,false);
                    JasperViewer.setDefaultLookAndFeelDecorated(true); 
                }
                else if(petugas.isSelected())
                {
                    File file = new File ("src/cetakPetugas.jasper");

                    JasperReport jp = (JasperReport)JRLoader.loadObject(file);
                    JasperPrint jasperprint = JasperFillManager.fillReport(jp, param, conn);
                    JasperViewer.viewReport(jasperprint,false);
                    JasperViewer.setDefaultLookAndFeelDecorated(true); 
                }              
            }
        }
        catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Terjadi Kesalahan Dalam Pemrosesan");
        } 
        catch(ClassNotFoundException e){
            JOptionPane.showMessageDialog(null, "JDBC Driver tidak ditemukan");
        } catch (JRException ex) {
            Logger.getLogger(menuForm.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_cetakdataActionPerformed

